    def recover_failed_transfers(self):
        """Retry failed transfers with exponential backoff"""
        failed_transfers = self.get_failed_transfers()
        for transfer in failed_transfers:
            self.retry_transfer(transfer)

    def retry_transfer(self, transfer_id):
        """Retry individual transfer with backoff"""
        for attempt in range(self.max_retries):
            try:
